socket.on("notification",function(b,a,d,c){if(a){$("#notification-modal").openModal();$("#name").text(b);$("#location").text("Capen Libray Room 16");$("#subject").text("Assignment 3");$("#image").attr("src",c);$("#accept-btn").on("click",function(e){console.log("accepting");socket.emit("send accept",b)});$("#decline-btn").on("click",function(e){console.log("declining");socket.emit("send decline",b)})}Push.create("Update from tutr!",{body:b+d,icon:"/img/tutrlogo.png",timeout:10000,onClick:function(){window.focus();this.close()}})});socket.on("student accept",function(a){Materialize.toast(a+" is on the way",4000)});socket.on("tutr_error",function(a){Materialize.toast(a,4000)});socket.on("tutor accept",function(){$(".fixed-action-btn").show()});socket.on("decline",function(){$("#denied-modal").openModal();$("#denied-message").text("Looks like your tutor cannot make it.")});socket.on("start session",function(){Materialize.toast("Your session has started",4000)});socket.on("end session",function(a){$(".fixed-action-btn").hide();$("#rating-modal").openModal();$("#price").text("$"+a)});